import{g as e,f as l,G as a}from"./common-bec3f312.js";const r='{"title":"Node多进程开发入门","frontmatter":{"date":"2022-04-06","title":"Node多进程开发入门","tags":["前端架构师","week4","脚手架","Node","多进程"],"describe":null},"headers":[{"level":2,"title":"学习路径","slug":"学习路径"},{"level":3,"title":"官方文档","slug":"官方文档"},{"level":3,"title":"什么是进程","slug":"什么是进程"},{"level":2,"title":"child_process 用法","slug":"child-process-用法"},{"level":3,"title":"异步","slug":"异步"},{"level":3,"title":"同步","slug":"同步"}],"relativePath":"docs/Front_arch/week4/03.md","lastUpdated":1660147426756.5005}';var i={};const s=a('<h2 id="学习路径"><a class="header-anchor" href="#学习路径" aria-hidden="true">#</a> 学习路径</h2><h3 id="官方文档"><a class="header-anchor" href="#官方文档" aria-hidden="true">#</a> 官方文档</h3><p>中文版：<a href="http://nodejs.cn/api/child_process.html" target="_blank" rel="noopener noreferrer">http://nodejs.cn/api/child_process.html</a></p><h3 id="什么是进程"><a class="header-anchor" href="#什么是进程" aria-hidden="true">#</a> 什么是进程</h3><blockquote><p>进程（Process）是计算机中的程序关于某数据集合上的一次运行活动，是系统进行资源分配和调度的基本单位，是<a href="https://baike.baidu.com/item/%E6%93%8D%E4%BD%9C%E7%B3%BB%E7%BB%9F" target="_blank" rel="noopener noreferrer">操作系统</a>结构的基础。</p></blockquote><p>进程的概念主要有两点：</p><ul><li>第一，进程是一个实体。每一个进程都有它自己的地址空间。</li><li>第二，进程是一个“执行中的程序”，存在嵌套关系。</li></ul><p><img src="https://oss.filway.cn/filway-blog/5fe4a402089c280b13970727.jpg" alt="process"></p><p>我们在 child_process 中创建的进程就是 Node.js 的子进程</p><p><img src="https://oss.filway.cn/filway-blog/5fe4a411085cf7d216000858.jpg" alt="sub-process"></p><h2 id="child-process-用法"><a class="header-anchor" href="#child-process-用法" aria-hidden="true">#</a> child_process 用法</h2><h3 id="异步"><a class="header-anchor" href="#异步" aria-hidden="true">#</a> 异步</h3><ul><li>exec</li><li>execFile</li><li>fork</li><li>spawn</li></ul><h3 id="同步"><a class="header-anchor" href="#同步" aria-hidden="true">#</a> 同步</h3><ul><li>execSync</li><li>execFileSync</li><li>spawnSync</li></ul>',15);i.render=function(a,r,i,t,c,h){return l(),e("div",null,[s])};export default i;export{r as __pageData};
